<?php	echo "<a href=".$config['FRoot'].">Home</a><br />\n";	echo "<form action='".$config['FRoot']."/search/' method='get'>\n";	echo "<input type='text' name='user' value='' placeholder='Username...'>\n";	echo "<input type='submit' value='Search'>\n";	echo "</form>\n";	if (!isset($headerRefer)) {		$headerRefer = $config["FRoot"];	}	set_error_handler(function() { /* ignore errors */ });	$headerRefer = bin2hex(mb_convert_encoding($headerRefer, 'UCS-2', 'UTF-8'));	restore_error_handler();	// Print either Hi %User% or Hi Guest, with logout and login respectively which will refer back to given link when visited	if ($_SESSION['loggedIn'] == 1) {		echo "Hi ".$_SESSION['username']."! <a href='".$config["FRoot"]."/logout/?refer=".$headerRefer."'>Logout</a> - <a href='".$config["FRoot"]."/user/".$_SESSION['username']."'>Profile</a><br />\n";	} else {		echo "Hi Guest! <a href='".$config["FRoot"]."/login/?refer=".$headerRefer."'>Login</a><br />\n";	}?>